public class _567_checkInclusion extends Asserts {

	public static void main(String[] args) {
		_567_checkInclusion app = new _567_checkInclusion();
		//"tor"
		// "actor"
		isTrue(app.checkInclusion("tor", "actor"));

		//"hello"
		// "ooolleoooleh"
		isTrue(app.checkInclusion("a", "a"));
		//""
		// ""
		isTrue(app.checkInclusion("",
			""));
		isFalse(app.checkInclusion("hello", "ooolleoooleh"));
		isTrue(app.checkInclusion("aaaa", "abababaaaabbbba"));
		isFalse(app.checkInclusion("ab", "eidboaoo"));
		isTrue(app.checkInclusion("ab", "eidbaooo"));
	}

	public boolean checkInclusion1(String s1, String s2) {
		int begin = 0;
		int end = s1.length();
		char[] chars = s1.toCharArray();
		Arrays.sort(chars);
		char[] s2Chars = s2.toCharArray();
		char[] tmpChars;
		while (end <= s2.length()) {
			tmpChars = new char[s1.length()];
			for (int i = begin, flag = 0; i < end; i++, flag++) {
				tmpChars[flag] = s2Chars[i];
			}
			Arrays.sort(tmpChars);
			if (Arrays.equals(chars,tmpChars)) {
				return true;
			} else {
				begin++;
				end++;
			}
		}
		return false;
	}

	public boolean checkInclusion(String s1, String s2) {
		int begin = 0;
		int end = s1.length();
		Map<Character, Integer> charMap = new HashMap<>();
		char[] chars = s1.toCharArray();
		for (int i = 0; i < chars.length; i++) {
			charMap.put(chars[i], charMap.get(chars[i]) == null ? 1 : charMap.get(chars[i]) + 1);
		}
		char[] s2Chars = s2.toCharArray();
		Map<Character,Integer> s2Map;
		while (end <= s2.length()) {
			s2Map = new HashMap<>();
			int maxDiffPos = -1;
			for (int i = begin; i < end; i++) {
				if (charMap.get(s2Chars[i]) == null) {
					if (i > maxDiffPos) {
						maxDiffPos = i;
					}
				}
				s2Map.put(s2Chars[i], s2Map.get(s2Chars[i]) == null ? 1 : s2Map.get(s2Chars[i]) + 1);
			}
			if (maxDiffPos == -1) {
				int flag = -1;
				for (Character character : charMap.keySet()) {
					if (!charMap.get(character).equals(s2Map.get(character)) ){
						flag = 1;
						break;
					}
				}
				if (flag == -1) {
					return true;
				} else {
					begin++;
					end++;
				}
			} else {
				begin = maxDiffPos + 1;
				end = begin + s1.length();
			}
		}
		return false;
	}

}
